package day06;

public class Day06Task1 {
    public static void main(String[] args) {
        for (int task = 0; task < 2; task++) {
            int markerLength = task == 0 ? 4 : 14;
            findMarker: for (int i = 0; i < INPUT.length() - markerLength; i++) {
                String possibleMarker = INPUT.substring(i, i + markerLength);
                for (int j = 0; j < markerLength - 1; j++) {
                    char first = possibleMarker.charAt(j);
                    for (int k = j + 1; k < markerLength; k++) {
                        if (possibleMarker.charAt(k) == first) {
                            continue findMarker;
                        }
                    }
                }
                System.out.println((i + markerLength) + " - " + possibleMarker);
                break;
            }
        }
    }

    private static final String INPUT = "";
}
